public class IfConditionDemo3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int temp = 50;

		if (temp >= 60) {
			System.out.println("I am going to beach");
		} else {
			System.out.println("I will go for a walk");
		}

	
	}

}
